import React, { useState, useRef } from "react";
import Topbar from "../components/Topbar/Topbar";
import FormWithSection from "../components/Form/FormWithSection/FormWithSection";
import FormSection from "../components/Form/FormWithSection/FormSection";
import FormControl from "../components/Form/FormControl/FormControl";
import Input from "../components/Form/Input/Input";
import Select from "../components/Form/Input/Select";
import FormFooter from "../components/Form/FormFooter/FormFooter";
import Button from "../components/Form/Button/Button";
import DataList from "../components/Form/Input/DataList";

function createSelectData(optionValue, optionText) {
  return { optionValue, optionText };
}

//Sample Data for Select Input
const selectSampleData = [
  createSelectData("1", "description 1"),
  createSelectData("2", "description 2"),
  createSelectData("3", "description 3"),
  createSelectData("4", "description 4"),
  createSelectData("5", "description 5"),
];

//Sample Data for Datalist Input
const dataListSampleData = [
  "Dr. Shwe Yi",
  "Dr. Mya Theingi",
  "Dr. Park Soon Ri",
  "Dr. William Smith",
  "Dr. Hsu Wai Khet Khaing",
  "Dr. Min Min Soe",
  "Dr. Khin Hinn Wai",
  "Dr. Aung Ko Khant",
];

const VisitRegSetup = () => {
  console.log("visit setup");
  const [information, setInformation] = useState({
    name: "",
    fatherName: "",
  });
  const idRef = useRef();
  const nrcRef = useRef();
  const genderRef = useRef();
  const ageRef = useRef();
  const maritalStatusRef = useRef();
  const dateOfBirthRef = useRef();
  const placeOfBirthRef = useRef();

  //handle on change input
  const handleOnChange = (e) => {
    e.persist();
    setInformation((information) => ({
      ...information,
      [e.target.name]: e.target.value,
    }));
  };
  // handle on submit
  const handleOnSaveVR = (e) => {
    e.preventDefault();
    console.log(information);
    console.log(idRef.current.value);
    console.log(nrcRef.current.value);
    console.log(genderRef.current.value);
    console.log(ageRef.current.value);
    console.log(maritalStatusRef.current.value);
    console.log(dateOfBirthRef.current.value);
    console.log(placeOfBirthRef.current.value);
  };

  return (
    <React.Fragment>
      <Topbar pageTitle="Visit Registration Setup" />
      <div className="my-4 px-4 lg:px-0 w-full lg:w-4/5 xl:w-2/3 m-auto">
        <FormWithSection action="" onSubmit={handleOnSaveVR}>
          <FormSection sectionTitle="Personal Information">
            <FormControl className="flex-grow md:flex-grow-0">
              <Input
                className="w-full md:w-auto"
                type="text"
                labelText="Patient ID"
                inputRef={idRef}
              />
            </FormControl>
            <div className="flex flex-wrap items-end">
              <FormControl>
                <Select
                  className="w-32"
                  labelText="nrc"
                  optionData={selectSampleData}
                  preViewData="Initial"
                  required={true}
                  inputRef={nrcRef}
                />
              </FormControl>
              <FormControl className="flex-grow">
                <Input
                  className="w-full"
                  type="text"
                  labelText="Patient Name"
                  required={true}
                  name="name"
                  value={information.name}
                  onChange={handleOnChange}
                />
              </FormControl>
            </div>
            <FormControl className="flex-grow md:flex-grow-0">
              <Input
                className="w-full"
                type="text"
                labelText="Father Name"
                required={true}
                name="fatherName"
                value={information.fatherName}
                onChange={handleOnChange}
              />
            </FormControl>
            <div className="flex flex-wrap items-end">
              <FormControl>
                <Select
                  labelText="Gender"
                  optionData={selectSampleData}
                  preViewData="Other"
                  inputRef={genderRef}
                />
              </FormControl>
              <FormControl>
                <Select
                  labelText="Marital Status"
                  optionData={selectSampleData}
                  preViewData="Single"
                  inputRef={maritalStatusRef}
                />
              </FormControl>
            </div>
            <div className="flex flex-wrap items-end">
              <FormControl className="inline-block">
                <Input
                  className="w-24"
                  type="number"
                  labelText="Age"
                  inputRef={ageRef}
                />
              </FormControl>
              <FormControl className="inline-block">
                <Input
                  type="text"
                  labelText="Date of Birth"
                  placeholder="DD/MM/YYYY"
                  inputRef={dateOfBirthRef}
                />
              </FormControl>
            </div>
            <FormControl>
              <Input
                className="w-full"
                type="text"
                labelText="Place of Birth"
                inputRef={placeOfBirthRef}
              />
            </FormControl>
          </FormSection>
          <FormSection sectionTitle="Doctor Information">
            <FormControl>
              <DataList
                labelText="Doctors"
                placeholder="Select Doctors"
                dataList={dataListSampleData}
              />
            </FormControl>
          </FormSection>
          <FormFooter>
            <Button className="ml-2" type="">
              Save
            </Button>
          </FormFooter>
        </FormWithSection>
      </div>
    </React.Fragment>
  );
};

export default VisitRegSetup;
