import React, { useRef } from "react";
import FormSection from "../components/Form/FormWithSection/FormSection";
import FormControl from "../components/Form/FormControl/FormControl";
import Input from "../components/Form/Input/Input";
import Select from "../components/Form/Input/Select";

function createSelectData(optionValue, optionText) {
  return { optionValue, optionText };
}

//Sample Data for Select Input
const selectSampleData = [
  createSelectData("1", "description 1"),
  createSelectData("2", "description 2"),
  createSelectData("3", "description 3"),
  createSelectData("4", "description 4"),
  createSelectData("5", "description 5"),
];

const PersonalInformationForm = ({ information, handleOnChange }) => {
  return (
    <FormSection sectionTitle="Personal Information">
      <FormControl className="flex-grow md:flex-grow-0">
        <Input
          className="w-full md:w-auto"
          type="text"
          labelText="Patient ID"
          inputRef={idRef}
        />
      </FormControl>
      <div className="flex flex-wrap items-end">
        <FormControl>
          <Select
            className="w-32"
            labelText="nrc"
            optionData={selectSampleData}
            preViewData="Initial"
            required={true}
            inputRef={nrcRef}
          />
        </FormControl>
        <FormControl className="flex-grow">
          <Input
            className="w-full"
            type="text"
            labelText="Patient Name"
            required={true}
            name="name"
            value={information.name}
            onChange={handleOnChange}
          />
        </FormControl>
      </div>
      <FormControl className="flex-grow md:flex-grow-0">
        <Input
          className="w-full"
          type="text"
          labelText="Father Name"
          required={true}
          name="fatherName"
          value={information.fatherName}
          onChange={handleOnChange}
        />
      </FormControl>
      <div className="flex flex-wrap items-end">
        <FormControl>
          <Select
            labelText="Gender"
            optionData={selectSampleData}
            preViewData="Other"
            inputRef={genderRef}
          />
        </FormControl>
        <FormControl>
          <Select
            labelText="Marital Status"
            optionData={selectSampleData}
            preViewData="Single"
            inputRef={maritalStatusRef}
          />
        </FormControl>
      </div>
      <div className="flex flex-wrap items-end">
        <FormControl className="inline-block">
          <Input
            className="w-24"
            type="number"
            labelText="Age"
            inputRef={ageRef}
          />
        </FormControl>
        <FormControl className="inline-block">
          <Input
            type="text"
            labelText="Date of Birth"
            placeholder="DD/MM/YYYY"
            inputRef={dateOfBirthRef}
          />
        </FormControl>
      </div>
      <FormControl>
        <Input
          className="w-full"
          type="text"
          labelText="Place of Birth"
          inputRef={placeOfBirthRef}
        />
      </FormControl>
    </FormSection>
  );
};

export default PersonalInformationForm;
