import React, { useState, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Button from "../Button/Button";

const DataList = ({
  labelText,
  required,
  name,
  id,
  onFocus,
  placeholder,
  dataList,
}) => {
  const [selectedData, setSelectedData] = useState("");
  const doctorDataList = useRef();
  const myTextArea = useRef();
  const addSelectedData = () => {
    if (selectedData === "") {
      setSelectedData(doctorDataList.current.value);
      doctorDataList.current.value = "";
    } else {
      setSelectedData(selectedData + ", " + doctorDataList.current.value);
      doctorDataList.current.value = "";
    }
  };
  const updateSelectedData = (e) => {
    console.log("textarea change");
    setSelectedData(myTextArea.current.value);
  };

  return (
    <React.Fragment>
      {labelText ? (
        <div className="p-1">
          <label className="text-xs text-gray-700">{labelText}</label>
          {required === true && <span className="text-red-500 ml-1">*</span>}
        </div>
      ) : null}
      <textarea
        ref={myTextArea}
        placeholder="All data you selected will show here..."
        className="block w-full focus:outline-none h-32 resize-none p-4 mb-2 bg-gray-100 rounded-lg focus:shadow-inner focus:bg-white border border-gray-400"
        name={name}
        onChange={updateSelectedData}
        onFocus={onFocus}
        required={required}
        id={id}
        value={selectedData}
      ></textarea>
      <div className="flex flex-nowrap items-center">
        <div className="flex-grow">
          <input
            ref={doctorDataList}
            className="block w-full focus:outline-none h-10 px-4 bg-gray-100 rounded-lg focus:shadow-inner focus:bg-white border border-gray-400"
            list="dataList"
            placeholder={placeholder}
          />
          <datalist id="dataList">
            {dataList.map((dl, index) => (
              <React.Fragment key={index}>
                <option value={dl} />
              </React.Fragment>
            ))}
          </datalist>
        </div>
        <Button className="ml-4" type="button" onClick={addSelectedData}>
          <FontAwesomeIcon icon="plus" />
        </Button>
      </div>
    </React.Fragment>
  );
};

export default DataList;
