const PatientSetupValidate = (values, returnState = false) => {
  if (returnState) {
    if (values) {
      return "";
    } else {
      return values;
    }
  }
  let errors = {};
  if (!values.id) {
    errors.id = "ID field is required";
  } else {
    errors.id = "";
  }
  if (!values.nrc) {
    errors.nrc = "NRC field is required";
  } else {
    errors.nrc = "";
  }
  if (!values.name) {
    errors.name = "Name field is required";
  } else {
    errors.name = "";
  }
  if (!values.fatherName) {
    errors.fatherName = "Father name field is required";
  } else {
    errors.fatherName = "";
  }
  if (!values.gender) {
    errors.gender = "Gender field is required";
  } else {
    errors.gender = "";
  }
  if (!values.maritalStatus) {
    errors.maritalStatus = "Marital Status field is required";
  } else {
    errors.maritalStatus = "";
  }
  if (!values.age) {
    errors.age = "Age field is required";
  } else {
    errors.age = "";
  }
  if (!values.dateOfBirth) {
    errors.dateOfBirth = "Date of Birth field is required";
  } else {
    errors.dateOfBirth = "";
  }
  if (!values.placeOfBirth) {
    errors.placeOfBirth = "Place of Birth field is required";
  } else {
    errors.placeOfBirth = "";
  }
  if (!values.location) {
    errors.location = "Location field is required";
  } else {
    errors.location = "";
  }
  if (!values.email) {
    errors.email = "Email field is required";
  } else {
    errors.email = "";
  }
  if (!values.occupation) {
    errors.occupation = "Occupation field is required";
  } else {
    errors.occupation = "";
  }
  if (!values.bloodType) {
    errors.bloodType = "Blood Type field is required";
  } else {
    errors.bloodType = "";
  }
  if (!values.allergy) {
    errors.allergy = "Allergy field is required";
  } else {
    errors.allergy = "";
  }
  if (!values.remark) {
    errors.remark = "Remark field is required";
  } else {
    errors.remark = "";
  }
  return errors;
};

export default PatientSetupValidate;
