import React, { useState } from "react";
import TableFilter from "./TableFilter";
import TableHeader from "./TableHeader";
import TableDataRow from "./TableDataRow";
import TablePagination from "./TablePagination";

const style = {
  backdropBlur: {
    backdropFilter: "blur(3px)",
    WebkitBackdropFilter: "blur(3px)",
    background: "rgba(255, 255, 255, 0.95)",
  },
};

const Table = (props) => {
  const [currentMinRow, setCurrentMinRow] = useState(0);
  const [currentMaxRow, setCurrentMaxRow] = useState(props.rowLimit);
  const [resultData, setResultData] = useState(props.tableDataRow);
  const [isTableFilterOpen, setTableFilterOpen] = useState(false);

  const toggleTableFilter = () => {
    setTableFilterOpen(!isTableFilterOpen);
  };
  const loadNextData = () => {
    if (currentMaxRow < resultData.length) {
      setCurrentMinRow(currentMaxRow);
      setCurrentMaxRow(currentMaxRow + props.rowLimit);
    }
  };
  const loadPrevData = () => {
    if (currentMinRow > 0) {
      setCurrentMaxRow(currentMinRow);
      setCurrentMinRow(currentMinRow - props.rowLimit);
    }
  };
  const loadFistData = () => {
    if (currentMinRow > 0) {
      setCurrentMinRow(0);
      setCurrentMaxRow(props.rowLimit);
    }
  };
  const loadLastData = () => {
    if (currentMaxRow < resultData.length) {
      setCurrentMinRow(
        resultData.length - (resultData.length % props.rowLimit)
      );
      setCurrentMaxRow(resultData.length);
    }
  };
  const filteredData = (query) => {
    let data = props.tableDataRow.filter((tdr) =>
      tdr.reduce((prev, next) => (prev + next).toLowerCase()).includes(query)
    );
    setResultData(data);
  };
  return (
    <div className="overflow-auto pb-10 text-sm md:border md:bg-white md:rounded-lg md:shadow-md md:overflow-hidden md:pb-0">
      <div
        style={style.backdropBlur}
        className="fixed bottom-0 left-0 p-4 w-full border-t text-center md:flex md:items-center md:p-1 md:justify-between md:static md:w-auto md:border-none z-20"
      >
        {this.props.tableFilter && (
          <div
            className={`-mt-2 mb-3 md:mb-0 md:mt-0 ${
              this.state.isTableFilterOpen ? null : "hidden md:block"
            }`}
          >
            <TableFilter filteredData={this.filteredData} />
          </div>
        )}
        <TablePagination
          tableFilter={this.props.tableFilter}
          toggleTableFilter={this.toggleTableFilter}
          isTableFilterOpen={this.state.isTableFilterOpen}
          loadNextData={this.loadNextData}
          loadPrevData={this.loadPrevData}
          loadFistData={this.loadFistData}
          loadLastData={this.loadLastData}
          rowCount={this.state.resultData.length}
          minRow={this.state.currentMinRow}
          maxRow={this.state.currentMaxRow}
        />
      </div>
      <div className="block md:table w-full">
        <TableHeader
          tableHeader={this.props.tableHeader}
          tableAction={this.props.tableAction}
        />
        {this.state.resultData
          .slice(this.state.currentMinRow, this.state.currentMaxRow)
          .map((row, index) => (
            <React.Fragment key={index}>
              <TableDataRow
                tableHeader={this.props.tableHeader}
                tableDataCol={row}
                rowCount={index + this.state.currentMinRow + 1}
                tableAction={this.props.tableAction}
              />
            </React.Fragment>
          ))}
      </div>
    </div>
  );
};

export default Table;
