import React, { useState, useCallback } from "react";
import Topbar from "../components/Topbar/Topbar";
import FormWithSection from "../components/Form/FormWithSection/FormWithSection";
import FormSection from "../components/Form/FormWithSection/FormSection";
import FormControl from "../components/Form/FormControl/FormControl";
import Input from "../components/Form/Input/Input";
import Select from "../components/Form/Input/Select";
import TextArea from "../components/Form/Input/TextArea";
import CheckBox from "../components/Form/Input/Checkbox";
import FormFooter from "../components/Form/FormFooter/FormFooter";
import Button from "../components/Form/Button/Button";

function createInitialName(optionValue, optionText) {
  return { optionValue, optionText };
}

//Sample Data for Select Input
const sampleData = [
  createInitialName("1", "description 1"),
  createInitialName("2", "description 2"),
  createInitialName("3", "description 3"),
  createInitialName("4", "description 4"),
  createInitialName("5", "description 5"),
];

const PatientSetup = ({ toggleSidebar }) => {
  console.log("patient setup");
  const [personalInformation, setPersonalInformation] = useState({
    id: "",
    nrc: "",
    name: "",
    fatherName: "",
    gender: "",
    maritalStatus: "",
    age: "",
    dateOfBirth: "",
    placeOfBirth: "",
  });
  const handleOnChange = useCallback(
    (e) => {
      console.log(personalInformation);
      setPersonalInformation({
        ...personalInformation,
        [e.target.name]: e.target.value,
      });
      console.log(personalInformation);
    },
    [personalInformation]
  );
  const handleOnSavePatient = (e) => {
    e.preventDefault();
    console.log("handleOnSavePatient");
  };
  return (
    <React.Fragment>
      <Topbar toggleSidebar={toggleSidebar} pageTitle="Patient Setup" />
      <div className="my-4 px-4 lg:px-0 w-full lg:w-4/5 xl:w-2/3 m-auto">
        <FormWithSection action="" onSubmit={handleOnSavePatient}>
          <FormSection sectionTitle="Personal Information">
            <FormControl className="flex-grow md:flex-grow-0">
              <Input
                className="w-full md:w-auto"
                type="text"
                labelText="Patient ID"
                name="id"
                value={personalInformation.id}
                onChange={(e) =>
                  setPersonalInformation({
                    ...personalInformation,
                    id: e.target.value,
                  })
                }
              />
            </FormControl>
            <div className="flex flex-wrap items-end">
              <FormControl>
                <Select
                  className="w-32"
                  labelText="nrc"
                  optionData={sampleData}
                  preViewData="Initial"
                  required={true}
                  name="nrc"
                  value={personalInformation.nrc}
                  onChange={handleOnChange}
                />
              </FormControl>
              <FormControl className="flex-grow">
                <Input
                  className="w-full"
                  type="text"
                  labelText="Patient Name"
                  required={true}
                  name="name"
                  value={personalInformation.name}
                  onChange={(e) =>
                    setPersonalInformation({
                      ...personalInformation,
                      name: e.target.value,
                    })
                  }
                />
              </FormControl>
            </div>
            <FormControl className="flex-grow md:flex-grow-0">
              <Input
                className="w-full"
                type="text"
                labelText="Father Name"
                required={true}
                name="fatherName"
                value={personalInformation.fatherName}
                onChange={handleOnChange}
              />
            </FormControl>
            <div className="flex flex-wrap items-end">
              <FormControl>
                <Select
                  labelText="Gender"
                  optionData={sampleData}
                  preViewData="Other"
                  name="gender"
                  value={personalInformation.gender}
                  onChange={handleOnChange}
                />
              </FormControl>
              <FormControl>
                <Select
                  labelText="Marital Status"
                  optionData={sampleData}
                  preViewData="Single"
                  name="maritalStatus"
                  value={personalInformation.maritalStatus}
                  onChange={handleOnChange}
                />
              </FormControl>
            </div>
            <div className="flex flex-wrap items-end">
              <FormControl className="inline-block">
                <Input
                  className="w-24"
                  type="number"
                  labelText="Age"
                  name="age"
                  value={personalInformation.age}
                  onChange={handleOnChange}
                />
              </FormControl>
              <FormControl className="inline-block">
                <Input
                  type="text"
                  labelText="Date of Birth"
                  placeholder="DD/MM/YYYY"
                  name="dateOfBirth"
                  value={personalInformation.dateOfBirth}
                  onChange={handleOnChange}
                />
              </FormControl>
            </div>
            <FormControl>
              <Input
                className="w-full"
                type="text"
                labelText="Place of Birth"
                name="placeOfBirth"
                value={personalInformation.placeOfBirth}
                onChange={handleOnChange}
              />
            </FormControl>
          </FormSection>
          <FormSection sectionTitle="Address Information">
            <FormControl>
              <Input className="w-full" type="text" labelText="Location" />
            </FormControl>
            <FormControl>
              <Input className="w-full" type="email" labelText="Email" />
            </FormControl>
            <FormControl>
              <Input className="w-full" type="text" labelText="Occupation" />
            </FormControl>
          </FormSection>
          <FormSection sectionTitle="Medical Information">
            <FormControl>
              <Select
                className="w-24"
                labelText="Blood Type"
                optionData={sampleData}
                preViewData="-"
              />
            </FormControl>
            <FormControl>
              <TextArea className="w-full" labelText="Allergy" />
            </FormControl>
          </FormSection>
          <FormSection sectionTitle="Remark">
            <FormControl>
              <TextArea className="w-full" />
            </FormControl>
            <FormControl>
              <CheckBox labelText="Active" />
            </FormControl>
          </FormSection>
          <FormFooter>
            <Button className="ml-2" type="button">
              Save & Print
            </Button>
            <Button className="ml-2" type="submit">
              Save
            </Button>
          </FormFooter>
        </FormWithSection>
      </div>
    </React.Fragment>
  );
};

export default PatientSetup;
